function Icons(props: { className: string; name: string; width: number; height: number; fill?: string; }) {
    const { className, name, width, height, fill } = props;
    const ref = General[name];
    return (
        <svg
            className={className}
            width={width}
            height={height}
            viewBox={`0 0 ${ref.viewport.vw} ${ref.viewport.vh}`}
            fill={fill || '#cccccc'}
        >
            {ref.path.map((item, id) => (
                <path d={item} key={id} />
            ))}
        </svg>
    );
}

function CIcons(props: { className: string; name: string; width: number; height: number; }) {
    const { className, name, width, height } = props;
    const ref = Colorful[name];
    return (
        <svg
            className={className}
            width={width}
            height={height}
            viewBox={`0 0 1024 1024`}
        >
            {ref.path.map((item, id) => (
                <path d={item.d} fill={item.fill} key={id} />
            ))}
        </svg>
    );
}

